function gosterMetin(index) {
    const diyet = diyetVerileri[index];
    document.getElementById('baslik').textContent = diyet.baslik;
    
    // Başlıkları ve içeriği işle
    const basliklar = [];
    const icerik = diyet.icerik.split('\n').map(line => {
        // Eğer satır sonu boşsa veya sadece boşluk karakterlerinden oluşuyorsa
        if (!line.trim()) return line;
        
        // Eğer satır sonu soru işareti içeriyorsa veya satır başında belirli kelimeler varsa
        if (line.trim().endsWith('?') || 
            line.trim().startsWith('Dukan Diyeti') ||
            line.trim().includes('Aşama') ||
            line.trim().includes('Nasıl Yapılır') ||
            line.trim().includes('İlkeleri') ||
            line.trim().includes('Kuralları') ||
            line.trim().includes('Listesi') ||
            line.trim().includes('Faydaları') ||
            line.trim().includes('Zararları') ||
            line.trim().includes('Çeşitleri') ||
            line.trim().includes('Önemi') ||
            line.trim().includes('Evreleri') ||
            line.trim().includes('Tedavisi') ||
            line.trim().includes('Etkileri') ||
            line.trim().includes('Katkıları') ||
            line.trim().includes('Yanlışlar') ||
            line.trim().includes('Besinler') ||
            line.trim().includes('Sıkça Sorulan')) {
            
            // Başlığı linkler için kaydet
            const baslik = line.trim();
            const baslikId = baslik.toLowerCase()
                .replace(/\s+/g, '-')
                .replace(/[^a-z0-9-]/g, '')
                .replace(/-+/g, '-');
            basliklar.push({ text: baslik, id: baslikId });
            
            return `<strong id="${baslikId}">${baslik}</strong>`;
        }
        return line;
    }).join('\n');

    // İçeriği güncelle
    document.getElementById('icerik').innerHTML = icerik;
    document.getElementById('diyetResim').src = diyet.resim;
    document.getElementById('diyetResim').alt = diyet.baslik + ' resmi';

    // Başlık linklerini oluştur
    const linklerHTML = basliklar.map(baslik => 
        `<a href="#${baslik.id}" class="baslik-link">${baslik.text}</a>`
    ).join('');
    
    // Linkleri ekle
    const linklerDiv = document.querySelector('.baslik-linkleri');
    if (linklerDiv) {
        linklerDiv.innerHTML = linklerHTML;
    }
}

// Sayfa yüklendiğinde ilk diyeti göster
document.addEventListener('DOMContentLoaded', () => {
    gosterMetin(0);
});